<?php 

    $img = "";
    $imagenes = base64_decode($img);
    $source= imagecreatefromstring($imagenes);
    $angle=90;
    $rotate = imagerotate($source,$angle,0);
    $imageSave = imagejpeg($rotate,"prueba.jpg",100);
    imagedestroy($source);
   

    echo "<img src='".$imageSave."'></img>";
?>